    };

    const selectionsColumn = () => {
        if (Unit1SelectionSupport) {
            return Unit1SelectionSupport.map((item, index) => {
                return (
                    <React.Fragment key={index}>
                        <div className={`info-row ${stripes(index)} tall`}>
